tinymce.PluginManager.add('stories', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('stories', {
        text: 'Stories',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Promoted stories',
                body: [
                    {type: 'textbox', name: 'tag', label: 'filter | tag'},
                    {type: 'textbox', name: 'number', label: 'limit (enter number)'}
                ],
                onsubmit: function(e) {
					
					var token = document.getElementById("token").value;
					var cms_dir = document.getElementById("cms_dir").value;

					$.getJSON(cms_dir+"/cms/pages_ajax.php?action=getStories&token="+token+"&tag="+e.data.tag+"&number="+e.data.number+"", function(data){
						
						$.each(data, function(i,item){
                            var page = item.pages_id;
                            var title = item.title.length > 0 ? '<p>' + item.title + '</p>' : '';
                            var image = item.filename.length > 0 ? '<img src=' + cms_dir + '/content/uploads/pages/' + item.filename +'>' : ''; 
							editor.insertContent(title + image);
						});
						
					});                    
                }
            });
        }
    });

    // Adds menu item to the tools menu
    editor.addMenuItem('stories', {
        text: 'Stories',
        context: 'tools',
        onclick: function() {
            editor.insertContent('<emstart>EM Start</emstart><p>Example text!</p><emend>EM End</emend>');
        }
    });
});