tinymce.PluginManager.add('stories', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('stories', {
        text: 'Stories',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Enter number of stories',
                body: [
                    {type: 'textbox', name: 'number', label: '1-10'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
					editor.insertContent('Stories<p>' + e.data.number + '</p>');
					
					var token = document.getElementById("token").value;
					var cms_dir = document.getElementById("cms_dir").value;
					console.log("token", token);
					console.log("cms_dir", cms_dir);

					$.getJSON(cms_dir+"/cms/pages_ajax.php?action=getStories&token="+token+"&number="+e.data.number+"", function(data){
						
						$.each(data, function(i,item){
							var page = item.pages_id;
							if (item.title.length) {
								editor.insertContent('Stories<p>' + item.title + '</p><img src=' + cms_dir + '/content/uploads/pages/' + item.filename +'>');
							}
							
						});
						
					});
										
                    
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('stories', {
        text: 'Stories',
        context: 'tools',
        onclick: function() {
            editor.insertContent('<emstart>EM Start</emstart><p>Example text!</p><emend>EM End</emend>');
        }
    });
});